<?phpif (!empty($_SERVER['SCRIPT_FILENAME']) && 'comments.php' == basename($_SERVER['SCRIPT_FILENAME']))	die ('Please do not load this page directly. Thanks!');	if ( post_password_required() ) { ?>		<p class="nocomments">This post is password protected. Enter the password to view comments.</p>	<?php		return;	}	?><div id="comments"><?php if ( have_comments() ) : ?>	<h2><?php comments_number('No Responses', 'One Response', '% Responses' );?></h2>	<ol class="commentlist">		<?php wp_list_comments('type=comment&callback=custom_comment'); ?>	</ol><?php else : // this is displayed if there are no comments so far ?>	<?php if ( comments_open() ) : ?>		<!-- If comments are open, but there are no comments. -->	 <?php else : // comments are closed ?>		<!-- If comments are closed. -->	<?php endif; ?><?php endif; ?><?php if ( comments_open() ) : ?>	<div id="respond">		<h3><?php comment_form_title( 'Leave a Reply', 'Leave a Reply to %s' ); ?></h3><div class="cancel-comment-reply">	<small><?php cancel_comment_reply_link(); ?></small></div><?php if ( get_option('comment_registration') && !is_user_logged_in() ) : ?>	<p>You must be <a href="<?php echo wp_login_url( get_permalink() ); ?>">logged in</a> to post a comment.</p><?php else : ?>	<form action="<?php echo get_option('siteurl'); ?>/wp-comments-post.php" method="post" id="commentform">	<?php if ( is_user_logged_in() ) : ?>    	<p>Logged in as <a href="<?php echo get_option('siteurl'); ?>/wp-admin/profile.php"><?php echo $user_identity; ?></a>. <a href="<?php echo wp_logout_url(get_permalink()); ?>" title="Log out of this account">Log out &raquo;</a></p>	<?php else : ?>    	<p><label for="author">Your Name</label><input type="text" class="text" name="author" id="author" value="<?php echo esc_attr($comment_author); ?>" size="37" tabindex="1" <?php if ($req) echo "aria-required='true'"; ?>></p>		<p><label for="email">Your E-Mail Address</label><input type="text" class="text" name="email" id="email" value="<?php echo esc_attr($comment_author_email); ?>" size="37" tabindex="2" <?php if ($req) echo "aria-required='true'"; ?>> <em>Will not be published.</em></p>		<p><label for="url">Your Website Address</label><input type="text" class="text" name="url" id="url" value="<?php echo esc_attr($comment_author_url); ?>" size="37" tabindex="3"> <em>Optional</em></p>	<?php endif; ?>		<p><textarea name="comment" id="comment" cols="58" rows="10" tabindex="4"></textarea></p>		<p><input name="submit" type="submit" id="submit" tabindex="5" value="Submit Comment">		<?php comment_id_fields(); ?></p>				<?php do_action('comment_form', $post->ID); ?>	</form>	<?php endif; // If registration required and not logged in ?>	</div><?php endif; // if you delete this the sky will fall on your head ?></div>