<ul class="pagination">  <!-- the previous page -->  <?php pp_the_pagination();			if (pp_has_previous_page()) : ?>  <li class="previous"><a href="<?php pp_the_previous_page_permalink(); ?>" title="View newer posts.">&laquo; Newer</a></li>  <?php else : ?>  <li class="disabled"><span title="There are no newer posts.">&laquo; Newer</span></li>  <?php endif;						pp_rewind_pagination(); ?>  <!-- the page links -->  <?php while(pp_has_pagination()) : pp_the_pagination();				if (pp_is_current_page()) : ?>  <li class="active number"><span title="You are viewing page <?php pp_the_page_num(); ?>">    <?php pp_the_page_num(); ?>    </span></li>  <?php else : ?>  <li class="number"><a href="<?php pp_the_page_permalink(); ?>" title="View page <?php pp_the_page_num(); ?>">    <?php pp_the_page_num(); ?>    </a></li                >  <?php endif; ?>  <?php endwhile; pp_rewind_pagination(); ?>  <!-- the next page -->  <?php pp_the_pagination(); if (pp_has_next_page()) : ?>  <li class="next"><a href="<?php pp_the_next_page_permalink(); ?>" title="View older posts.">Older &raquo;</a>    <?php else : ?>  <li class="disabled next"><span title="There are no older posts.">Older &raquo;</span></li>  <?php endif; pp_rewind_pagination(); ?></ul>